import styled, { css } from "styled-components";
import { TaskTitleProps } from "../types/taskResult.type";

export const TaskListContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  margin: 0 auto;
  width: 60%;
`;

export const TaskTitle = styled.div<TaskTitleProps>`
  justify-content: flex-start;
  padding: 5px;

  ${(props) =>
    props.$completed &&
    css`
      border: 2px solid ${({ theme }) => theme.secondary};
      border-radius: 10px 10px 10px 10px;
      text-decoration: line-through;
    `}
`;

export const TaskContainer = styled.div`
  margin: 10px 0;
  width: 100%;
  display: flex;
  justify-content: space-between;
`;

export const DeleteCheckboxContainer = styled.div`
  width: 30%
  display: flex;
  justify-content: center;
  align-items:center;
`;

export const Input = styled.input`
  margin: auto 15px;

    width: ${({ theme }) => theme.fontSizButtonMobile};
    height: ${({ theme }) => theme.fontSizButtonMobile};
  ${({ theme }) => theme.tablet} {
    padding: 8px 12px;
    width: ${({ theme }) => theme.fontSizButtonMobileTablet};
    height: ${({ theme }) => theme.fontSizButtonMobileTablet};
  }

  ${({ theme }) => theme.desktop} {
    width: ${({ theme }) => theme.fontSizButtonMobileDesktop};
    height: ${({ theme }) => theme.fontSizButtonMobileDesktop};
  }

  ${({ theme }) => theme.bigDesktop} {
    width ${({ theme }) => theme.fontSizButtonMobileBigDesktop};
    height: ${({ theme }) => theme.fontSizButtonMobileBigDesktop};
  }
`;
