import "./App.css";
import AddTask from "./components/addTask/addTask";
import { useHttpRequest } from "./components/hooks/httpRequest";
import { MainWrapper, Title } from "./components/styles/tasks.styles";
import TaskResult from "./components/taskResult/taskResult";

function App() {
  const {
    handleAddTask,
    handleRemoveTask,
    handleToggleTask,
    tasks,
    newTask,
    setNewTask,
  } = useHttpRequest();

  return (
    <MainWrapper>
      <Title>Lista zadań: </Title>
      <AddTask
        setNewTask={setNewTask}
        handleAddTask={handleAddTask}
        newTask={newTask}
      />
      {tasks.length === 0 ? (
        <h2>Nie dodano jeszcze żadnych zadań</h2>
      ) : (
        <TaskResult
          handleRemoveTask={handleRemoveTask}
          handleToggleTask={handleToggleTask}
          tasks={tasks}
        />
      )}
    </MainWrapper>
  );
}

export default App;
